+import { doc, getDoc } from "@firebase/firestore";
+import { GetStaticProps } from "next";
+import Listings from "../components/listings";
+import { firestore } from "../modules/firebase/initialiseFirebase";
+
+export const getStaticProps: GetStaticProps = async () =>  {
+  const response = await getDoc(doc(firestore, "categories", "skills"))
+
+  const Categories = response['_document'].data.value.mapValue.fields.skills.arrayValue.values
+  
+    
+    return {
+      props: {Categories}
+    }
+    
+  }
+
+
+export default function Categories({Categories}) {
+
+    
+
+    return (
+<div className="p-10">
+<div className="bg-transparent min-h-screen py-12 px-10 ">
+            {props.title && <h3 className="text-primary text-center mb-5 text-3xl font-bold">
+                {props.title}
+            </h3>}
+            {props.featured && <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 md:gap-x-10 xl-grid-cols-4 gap-y-10 gap-x-6 ">
+
+
+                {props.featured.map(data => (
+
+                    <Card
+                        link={'profiles/' + data.displayName.stringValue}
+                        image={data.cardImageURL.stringValue}
+                        skill={data.skill.stringValue}
+                        hiveName={data.hiveName.stringValue}
+                        description={data.description.stringValue}
+                        rating={data.rating.stringValue}
+                        projects={data.projects.integerValue}
+                    />
+                ))
+                }
+
+
+            </div>}
+
+
+            </div>
+       
+</div>
+    );
+}
