+import axios from "axios";
+import { useAuthState } from "react-firebase-hooks/auth";
+import { auth } from "../../modules/firebase/initialiseFirebase";
+
+export default function () {
+    const [user, loading, error] = useAuthState(auth)
+    const header = { 'Private-Key': process.env.CHAT_ENGINE_PRIVATE_KEY }
+    
+    const createChatUser = () => {
+        axios.post('https://api.chatengine.io/users/', {
+            body: {
+                username: 'jonhan17@gmail.com',
+                secret: 'password'
+            },
+            headers: { 'private-key': process.env.CHAT_ENGINE_PRIVATE_KEY }
+            })
+        }
+        createChatUser()
+
+        async function getFile(url) {
+            let response = await fetch(url);
+            let data = await response.blob();
+            return new File([data], "test.jpg", { type: 'image/jpeg' });
+          }
+        
+          useEffect(() => {
+            if (!didMountRef.current) {
+              didMountRef.current = true
+        
+              if (!user || user === null) {
+                history.push("/")
+                return
+              }
+              
+              // Get-or-Create should be in a Firebase Function
+              axios.get(
+                'https://api.chatengine.io/users/me/',
+                { headers: { 
+                  "project-id": '784bdb9e-8724-4f63-8ab6-3c10d59f74a7',
+                  "user-name": user.email,
+                  "user-secret": user.uid
+                }}
+              )
+        
+              .then(() => setLoading(false))
+        
+              .catch(e => {
+                let formdata = new FormData()
+                formdata.append('email', user.email)
+                formdata.append('username', user.email)
+                formdata.append('secret', user.uid)
+        
+                getFile(user.photoURL)
+                .then(avatar => {
+                  formdata.append('avatar', avatar, avatar.name)
+        
+                  axios.post(
+                    'https://api.chatengine.io/users/',
+                    formdata,
+                    { headers: { "private-key": process.env.REACT_APP_CHAT_ENGINE_KEY }}
+                  )
+                  .then(() => setLoading(false))
+                  .catch(e => console.log('e', e.response))
+                })
+              })
+        
+            }
+          }, [user, history])
+          
+
+    return (
+        <div>
+            Chat
+        </div>
+    );
+}
